mapper.update:
UPDATE sys_config
configType = #{configType}, provider = #{provider}, configName = #{configName}, configDesc = #{configDesc}, appId = #{appId}, apiKey = #{apiKey}, apiSecret = #{apiSecret}, apiUrl = #{apiUrl}, isDefault = #{isDefault}, state = #{state},
WHERE configId = #{configId}

mapper.select:
SELECT , ,
FROM sys_config LEFT JOIN sys_role ON sys_role.ttsId = sys_config.configId LEFT JOIN sys_device ON sys_config.configId = sys_device.sttId WHERE sys_config.state = 1 AND sys_config.userId = #{userId} AND configType = #{configType}
AND provider = #{provider}
AND provider != 'coze'
AND configName LIKE CONCAT('%', #{configName}, '%')
