mapper.update:
UPDATE
    sys_code
SET
    audioPath = #{audioPath}
WHERE
    deviceId = #{deviceId}
    AND sessionId = #{sessionId}
    AND code = #{code}

mapper.insert:
INSERT INTO sys_device ( deviceId, deviceName, userId, modelId, sttId, roleId ) VALUES (
    #{deviceId}, #{deviceName}, #{userId}, #{modelId}, #{sttId}, #{roleId}
)
