mapper.update:
UPDATE
    sys_code
SET
    audioPath = #{audioPath}
WHERE
    deviceId = #{deviceId}
    AND sessionId = #{sessionId}
    AND code = #{code}

UPDATE sys_device
state = #{state} deviceName = #{deviceName}, configId = #{configId}, roleId = #{roleId},
WHERE deviceId = #{deviceId}

mapper.insert:
INSERT INTO sys_device ( deviceId, deviceName, userId ) VALUES (
    #{deviceId}, #{deviceName}, #{userId}
)
