mapper.select:
SELECT
FROM sys_template WHERE sys_template.state = 1 AND sys_template.userId = #{userId}
AND template_name like concat('%', #{templateName}, '%')
AND category = #{category}
ORDER BY isDefault, createTime DESC

mapper.update:
UPDATE sys_template
templateName = #{templateName}, templateDesc = #{templateDesc}, templateContent = #{templateContent}, category = #{category}, isDefault = #{isDefault}, state = #{state},
WHERE templateId = #{templateId} and userId = #{userId}
